#include<iostream>#include<cstdio>using namespace std;int main(){//	freopen("1.txt","r",stdin);	int T;	scanf("%d",&T);	while(T--){		int a,b,c,sum1=0,sum2=0;		scanf("%d%d%d",&a,&b,&c);		int t1=0,t2=0;		for(int i=0;i<a;i++){			sum1+=300*(t1*2+1);			t1++;		} 		for(int i=0;i<b;i++){			sum1+=100*(t1*2+1);			t1++;		}		for(int i=0;i<c;i++){			sum1+=50*(t1*2+1);			t1++;		}		for(int i=0;i<c;i++){			sum2+=50*(t2*2+1);			t2++;		} 		for(int i=0;i<b;i++){			sum2+=100*(t2*2+1);			t2++;		}		for(int i=0;i<a;i++){			sum2+=300*(t2*2+1);			t2++;		}		printf("%d %d\n",sum1,sum2);	}		return 0;} //3712、   2164、   4023、   3936、   3333、   3957、   3100、   1070、   3313、   2670、   2186、   1666、   2405、   2488、   1764、   2201、   3809、   2208、   3875、   1712、   2482、   2191、   3210、   1495、   1350、   1494、   2773、   1730、   2185、   1902、   3958、   3767、   1667、   1402、   2172、   1239、   1503、   3322、   1799、   2932、   3328、   1814、   2417、   1652、   1425、   3610、   1342、   3179、   1671、   2165、   4036、   1944、